mapper.update:
UPDATE t_basic_user_info
nick_name = #{user.nickName, jdbcType=VARCHAR}, telephone = #{user.telephone, jdbcType=VARCHAR}, password = #{user.password, jdbcType=VARCHAR}, avatar = #{user.avatar, jdbcType=VARCHAR}, if_authentic = #{user.ifAuthentic, jdbcType=BIT}
WHERE user_id = #{user.userId, jdbcType=VARCHAR}
